:root {
  font-size: calc(1vw + 1vh + .5vmin);

  font-family: var(--sans-font);
  --sans-font: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  --mono-font: "SFMono-Regular", Consolas, "Liberation Mono", Menlo, Courier, monospace;

  /* Colors */
  --light: #f6f3ea;
  --dark: #1b2b34;
  --red: #ec5f67;
}

*, *:before, *:after {
  box-sizing: border-box;

  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
}

body {
  padding: 0;
  margin: 0;

  font-size: 1.2rem;

  color: var(--dark);
  background-color: var(--light);
}

main {
  display: grid;
  grid-template-rows: 1fr 1fr;
  height: 100vh;
  width: 100vw;
}

main > * {
  padding: 1rem;
}

header {
  display: flex;
  align-items: flex-end;
  border-bottom: 1px solid var(--red);
}

nav img, nav svg {
  --img-size: 2rem;

  height: var(--img-size);
  width: var(--img-size);
  max-height: var(--img-size);
  max-width: var(--img-size);
}

section {
  display: flex;
  flex-direction: column;
  height: 100%;
  justify-content: space-between;
}

nav {
  padding: 1rem;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  justify-content: flex-end;
}

nav > * {
  padding-bottom: .5rem;
  display: flex;
  align-items: center;
  justify-items: center;
  margin-right: 1rem;
}

nav a {
  display: flex;
  flex-direction: row;
  align-items: center;
  text-decoration: none;
  color: inherit;
}

.expand::after {
  content: "\00a0";
}
.expand {
  display: inline-block;
  vertical-align: text-bottom;
  white-space: nowrap;
  overflow: hidden;
  max-width: 0;
  transition: max-width .5s, color .5s;
  color: var(--dark);
}
svg {
  transition: color .5s;
  fill: var(--red);
}

.expand-parent.active .expand {
  max-width: 10rem;
}

@media (max-width: 480px) {
  .expand {
    max-width: 100%;
  }
}


h1 {
  font-family: minion-pro-display,serif;
  font-weight: 400;
  font-style: normal;
}

h2 {
  font-family: minion-pro,serif;
  font-weight: 400;
  font-style: italic;

  color: var(--red);
}
